mapper.select:
select UNIT_EVL_CODE, UNIT_EVL_NM, UNIT_EVL_BEGIN_DT, UNIT_EVL_END_DT, CLAS_CODE
from unit_evl
where UNIT_EVL_CODE = #{data}

mapper.insert:
insert into gc(GC_CODE, CLAS_STDNT_CODE, SCRE, UNIT_EVL_CODE, GC_DATE)
values ((select 'GC' || trim(to_char(nvl(max(substr(gc_code,3))+1,10000001), '00000000'))
           from gc)
         , #{clasStdntCode}
         , 0
         , #{unitEvlCode}
         , sysdate
       )

mapper.update:
update gc
set scre = #{scre}
  , gc_date = sysdate
where clas_stdnt_code = #{clasStdntCode}
and unit_evl_code = #{unitEvlCode}

mapper.select:
select 	 QUES_NO, UNIT_EVL_CODE, CLAS_STDNT_CODE, ASWPER_CN
from 	 aswper
where 	 unit_evl_code = #{unitEvlCode}
and 	 clas_stdnt_code = #{clasStdntCode}
order by ques_no asc

mapper.delete:
delete from aswper
where unit_evl_code = #{unitEvlCode}
  and clas_stdnt_code = #{clasStdntCode}

delete from gc
where unit_evl_code = #{unitEvlCode}
  and clas_stdnt_code = #{clasStdntCode}

mapper.update:
update unit_evl
set unit_evl_nm = #{unitEvlNm}
   , unit_evl_begin_dt = #{unitEvlBeginDt}
   , unit_evl_end_dt =#{unitEvlEndDt}
where unit_evl_code = #{unitEvlCode}

mapper.select:
select CLAS_STDNT_CODE, CLAS_IN_NO, CMMN_STDNT_CLSF, CMMN_CLAS_PSITN_STTUS, CLAS_CODE, SCHUL_CODE, MBER_ID, CLAS_STDNT_JOIN_DATE, CLAS_STDNT_EXIT_DATE
from clas_stdnt cs
where cs.mber_id = #{mberId}
and    cs.clas_code = #{clasCode}

with sub as( select ue.unit_evl_code, ue.unit_evl_nm, unit_evl_begin_dt, unit_evl_end_dt, ue.clas_code, cs.clas_stdnt_code, cs.clas_in_no, cs.cmmn_stdnt_clsf, cs.cmmn_clas_psitn_sttus, cs.clas_stdnt_join_date, cs.clas_stdnt_exit_date, cs.mber_id, m.mber_nm, gc.gc_code, gc.scre, gc.gc_date from unit_evl ue left join clas_stdnt cs on ue.clas_code = cs.clas_code left outer join gc on gc.unit_evl_code = ue.unit_evl_code and gc.clas_stdnt_code = cs.clas_stdnt_code left join member m on m.mber_id = cs.mber_id ) select sub1.unit_evl_code, sub1.unit_evl_nm, sub1.unit_evl_begin_dt, sub1.unit_evl_end_dt, yet.cnt yet_cnt, don.cnt done_cnt, tot.cnt all_cnt, av.avg_clas_score, maxs.max_score, mins.min_score, q.ques_no, q.ques_ques, q.ques_cnsr, q.ques_explna, q.ques_allot, sub1.clas_in_no, sub1.mber_nm, sub1.clas_stdnt_code, sub1.mber_id, sub1.gc_code, sub1.gc_date, nvl(sub1.scre,0) scre, q.ques_no ap_no, nvl(ap.aswper_cn, ' ') aswper_cn from sub sub1 inner join ques q on q.unit_evl_code = sub1.unit_evl_code left outer join aswper ap on sub1.clas_stdnt_code = ap.clas_stdnt_code and ap.unit_evl_code = q.unit_evl_code and ap.ques_no = q.ques_no left outer join (select sub2.unit_evl_code, round(avg(nvl(sub2.scre,0)),1) avg_clas_score from sub sub2 where sub2.clas_code = #{clasCode} group by sub2.unit_evl_code ) av on av.unit_evl_code = sub1.unit_evl_code left outer join (select sub3.unit_evl_code, count(*) cnt from sub sub3 where sub3.clas_code = #{clasCode} group by sub3.unit_evl_code ) tot on tot.unit_evl_code = sub1.unit_evl_code left outer join (select sub4.unit_evl_code, count(*) cnt from sub sub4 where sub4.gc_code is not null and sub4.clas_code = #{clasCode} group by sub4.unit_evl_code ) don on don.unit_evl_code = sub1.unit_evl_code left outer join (select sub5.unit_evl_code, count(*) cnt from sub sub5 where sub5.gc_code is null and sub5.clas_code = #{clasCode} group by sub5.unit_evl_code ) yet on yet.unit_evl_code = sub1.unit_evl_code left outer join (select sub6.unit_evl_code, max(nvl(sub6.scre,0)) max_score from sub sub6 group by sub6.unit_evl_code ) maxs on maxs.unit_evl_code = sub1.unit_evl_code left outer join (select sub7.unit_evl_code, min(nvl(sub7.scre,0)) min_score from sub sub7 group by sub7.unit_evl_code ) mins on mins.unit_evl_code = sub1.unit_evl_code where 1 = 1 and sub1.clas_code = #{clasCode}
and sub1.unit_evl_code = #{unitEvlCode}
and sub1.mber_id = #{mberId}
order by sub1.unit_evl_end_dt desc, sub1.unit_evl_begin_dt desc, sub1.unit_evl_code asc, sub1.clas_in_no asc, q.ques_no asc
