mapper.select:
WITH T AS ( SELECT COUNT(*) OVER() - ROW_NUMBER() OVER(ORDER BY NTT_CODE DESC) + 1 AS RNUM , NTT_CODE , NTT_NM , NTT_CN , NTT_WRITNG_DT , NTT_STTEMNT_ACCMLT , NTT_ATCH_FILE_CODE , CMMN_NTT_SE , CMMN_NTT_STTEMNT , CLAS_CODE , SCHUL_CODE , M.MBER_ID , M.MBER_NM AS MBER_NAME , NTT_RDCNT FROM NTT JOIN MEMBER M ON NTT.MBER_ID = M.MBER_ID WHERE SCHUL_CODE = #{schulCode} AND CMMN_NTT_SE='A08102'
AND ( NTT_NM LIKE '%' || #{keyword} || '%' )
) SELECT T.* FROM T WHERE T.RNUM BETWEEN (#{currentPage}*10) - (10 - 1) AND (#{currentPage}*10)

mapper.insert:
